import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-recipe-server',
  templateUrl: './recipe-server.component.html',
  styleUrls: ['./recipe-server.component.css']
})
export class RecipeServerComponent {
  @Input() server: {type: string, name: string, content: string}

}
 <!--Cards-->

 <div class="card">
  <div class="card-header">
  {{server.name}}
  </div>
  <div class="card-body">

    <p class="card-text">
      <strong *ngIf="server.type === 'server'" style="color: red;">{{ server.content }}</strong>
      <em *ngIf="server.type==='blueprint'" >{{ server.content }}</em>
      </p>

  </div>
</div>
