import { Component, EventEmitter, Output } from '@angular/core';
import { Recipe } from './recipe.model';

@Component({
  selector: 'app-recipes',
  templateUrl: './recipes.component.html',
  styleUrls: ['./recipes.component.css']
})
export class RecipesComponent {
  @Output() recipeDetailEvent = new EventEmitter<Recipe>();

  recipeDetailRecipeSelected(recipe: Recipe) {
    this.recipeDetailEvent.emit(recipe)
  }

}
<!--Place recipe-list and recipe-details side by side in recipes component--->
<div class="row">
  <div class="col-md-5">
   <app-recipe-list (recipeListRecipeSelected)="recipeDetailRecipeSelected($event)"></app-recipe-list>
  </div>
  <div class="col-md-7">
    <app-recipe-details (recipeDetailEvent)="recipeSelected($event)"></app-recipe-details>
  </div>
</div>
