import { Component, EventEmitter, Output } from '@angular/core';
import { Recipe } from './recipe.model';

@Component({
  selector: 'app-recipes',
  templateUrl: './recipes.component.html',
  styleUrls: ['./recipes.component.css']
})
export class RecipesComponent {
  @Output() onRecipeDetailsRecipeSelected = new EventEmitter<Recipe>();

    recipeDetailsRecipeSelected(recipe: Recipe) {
      this.onRecipeDetailsRecipeSelected.emit(recipe)
    }
}
<!--Place recipe-list and recipe-details side by side in recipes component--->
<div class="row">
  <div class="col-md-5">
    <app-recipe-list (recipeListRecipeSelected)="recipeDetailsRecipeSelected($event)" ></app-recipe-list>
  </div>
  <div class="col-md-7">
    <app-recipe-details (onRecipeDetailsRecipeSelected)="onRecipeAdded($event)"></app-recipe-details>
  </div>
</div>
